mapper.update:
	UPDATE member
	SET
     IMAGE = #{imagePath}, 
     userThumbImg = #{userThumbImg}
    WHERE 
    	memberId=#{memberId}

mapper.select:
SELECT *
FROM homegym_board 
WHERE memberId = #{memberId}
	AND status = 'ready' ;

mapper.update:
	UPDATE 
		homegym_board
	SET
    	acceptYN = #{status}
	WHERE
		h_id = #{hId}

mapper.select:

  		SELECT *
  		FROM (
  				SELECT *
				FROM homegym_board 
				WHERE memberId = #{memberId}
					AND status = 'ready'
				ORDER BY h_id desc
			)as h1
  		LIMIT #{cri.skip},#{cri.amount}


  		SELECT *
  		FROM (
  				SELECT *
				FROM homegym_board
				WHERE memberId = #{memberId}
				ORDER BY h_id desc
			)as h1
  		LIMIT #{cri.skip}, #{cri.amount}


  		SELECT *
  		FROM (
  				SELECT b.memberId,b.h_title,b.h_addr,d.payYN
				FROM homegym_board b INNER JOIN homegym_detail d
				ON b.h_id = d.h_id
				WHERE lend_memberId= #{memberId}
			)as h2
  		LIMIT #{cri.skip}, #{cri.amount}
