mapper.update:
	UPDATE member
	SET
     IMAGE = #{imagePath}
    WHERE 
    	memberId=#{memberId}

	UPDATE 
		homegym_detail
	SET
    	agreeYN = #{status}
	WHERE
		d_id = #{dId}

mapper.select:

  		SELECT *
  		FROM (
  				SELECT *
				FROM homegym_board 
				WHERE memberId = #{memberId}
					AND status = 'ready'
				ORDER BY h_id desc
			)as h1
  		LIMIT #{cri.skip},#{cri.amount}


		SELECT *
		FROM (
				SELECT *
				FROM trainer_board
				WHERE memberId = #{memberId}
				)as tb
  		LIMIT #{cri.skip}, #{cri.amount}


		SELECT *
		FROM (
				SELECT b.h_title,b.h_addr,d.rental_date, d.d_id,d.agreeYN
				FROM homegym_board b INNER JOIN homegym_detail d
				ON b.h_id = d.h_id
                WHERE memberId = #{memberId}
			)as wh
		LIMIT #{cri.skip}, #{cri.amount}


  		SELECT *
  		FROM (
  				SELECT distinct b.h_id,b.h_title,b.h_addr,b.h_cnt
				FROM homegym_board b INNER JOIN homegym_detail d
				WHERE memberId = #{memberId}
			)as lh
  		LIMIT #{cri.skip}, #{cri.amount}


  		SELECT *
  		FROM (
  				SELECT b.memberId,b.h_title,b.h_addr,d.payYN,d.rental_date
				FROM homegym_board b INNER JOIN homegym_detail d
				ON b.h_id = d.h_id
				WHERE borrowerId= #{memberId}
			)as rh
  		LIMIT #{cri.skip}, #{cri.amount}
